import './App.css';
import Cards from './Components/Cards/Cards';
import { useEffect, useState } from 'react'


function App() {
  const [pokemons, setPokemons] = useState([]);
  //const [search, setSearch] = useState("")

    useEffect(()=> {
      async function getPokemon (id){
        console.log("id", id)
        let response = await fetch (`https://pokeapi.co/api/v2/pokemon/${id}`);
        let data = await response.json()
        const newPokemon = {
          name: data.name,
          image: data.sprites.front_default,
        }
        //here we need to use a call back function, because it
        //is async, and then is overwritting the pokemon before.
        //IF YOU NEED TO CALL A ASYNC MULTIPLE TIMES,
        //use the call back, then it will keep the previous
        setPokemons((prev)=> [...prev, newPokemon]);
      };
      for (let i =1; 1< 11; i++){
        getPokemon(i);
      }
    }, [])
console.log(pokemons)

  return (
    <div className="App">
    <h1>Pokemon</h1>
      <Cards/>
    </div>
  );
}

export default App;
